import React, {useRef, useState} from "react";

function RenderDungeon(){
     const [royalDecrees, setRoyalDecrees] = useState([]);
     const decreeRef = useRef();
     function submitDecree(e){
          e.preventDefault();
          const decreeArray = decreeRef.current.value.split(" ");
          switch(decreeArray[0]){
               case"roll":
                    const dice = decreeArray[1].split("d");
                    const numOfDice = dice[0]
                    const numOfSides = dice[1];
                    let dieRollArray=[];
                    for(let i = numOfDice; i--; i > 0)
                    {
                         dieRollArray.push(`d${numOfSides}: ${Math.round(Math.random()*numOfSides-1)+1}`);
                         console.log(dieRollArray);
                    }
                    const value = dieRollArray;
                    setRoyalDecrees([...royalDecrees,
                         {
                              "text": decreeRef.current.value,
                              "value":value
                         }
                    ])
                    break;
               default: 
                    setRoyalDecrees([...royalDecrees,
                         {
                              "text": "Not a valid decree.",
                              "value": -1
                         }
                    ])
          }
     };
     return(
          <div>
               {royalDecrees.map((decree, index)=>{
                    if(index >= royalDecrees.length-5)
                    return(
                         <div key={index}>
                              <p>{decree.text}</p>
                              <p>{decree.value}</p>
                         </div>
                    )
                    else return null;
               })}
          <form onSubmit={submitDecree}>
               <label htmlFor="DecreeBox" className="">Enter thy decree</label>
               <input name="DecreeBox" ref={decreeRef} />
          </form>
          </div>
     );
}

export default RenderDungeon;
